import React, { Component } from 'react'
import { Link,withRouter} from 'react-router-dom';
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { registerUser } from "../../Store/actions/authActions";
import classnames from "classnames";

class Signup extends Component {
    constructor() {
        super();

        this.state = {
            username: "",
            email: "",
            password: "",
            createdDate: "",
            errors:{}
        }

    }

    componentWillReceiveProps(nextProps) {
        if (nextProps.errors) {
          this.setState({
            errors: nextProps.errors
          });
        }
      }

    handleInputChange = e => {
        this.setState({ [e.target.id]: e.target.value });
      };


    handleSubmit = (e) => {
        e.preventDefault();

        const newUser = {
           username : this.state.username,
           email : this.state.email,
           password :this.state.password
        };

        this.props.registerUser(newUser, this.props.history); 
  };

    render() {

        const { errors } = this.state;

        return (
            <section className="signup">
              <div className="container2">
                <div className="signup-content">
                  <div className="signup-form">
                              <h2 className="form-title">Sign up</h2>
                              <form className="register-form" id="register-form" onSubmit={this.handleSubmit}>
                                  <div className="form-group">
                                      <label for="name"><i className="zmdi zmdi-accounts material-icons-name"></i></label>
                                      <input type="text" 
                                      name="username" 
                                      id="username" 
                                      placeholder="Your Name" 
                                      defaultValue={this.state.username} 
                                      error={errors.username} 
                                      className={classnames("", {
                                      invalid: errors.username
                                      })} 
                                      onChange={this.handleInputChange} />
                                      <span className="red-text">{errors.email}</span>
                                  </div>
                                  <div className="form-group">
                                      <label for="email"><i className="zmdi zmdi-email"></i></label>
                                      <input type="email" 
                                      name="email" 
                                      id="email" 
                                      placeholder="Your Email" 
                                      defaultValue={this.state.email} 
                                      error={errors.email} 
                                      className={classnames("", {
                                        invalid: errors.email
                                        })}
                                      onChange={this.handleInputChange} />

                                      <span className="red-text">{errors.email}</span>
                                  </div>
                                  <div className="form-group">
                                      <label for="pass"><i className="zmdi zmdi-lock"></i></label>
                                      <input type="password" 
                                      name="password" 
                                      id="password" 
                                      placeholder="Password" 
                                      defaultValue={this.state.password} 
                                      error={errors.password}
                                      className={classnames("", {
                                        invalid: errors.password
                                        })} 
                                      onChange={this.handleInputChange} />

                                      <span className="red-text">{errors.password2}</span>
                                  </div>

                                  <div className="form-group form-button">
                                  <input type="submit" name="signup" id="signup" className="form-submit" value= "Sign up"></input>
                                  </div>
                              </form>  
                          </div> 
                      <div>
                          <figure>
                            <img src="images/signup-image.jpg" alt="Sign up"></img>
                          </figure> 
                      </div>
                  </div>
              </div>
            </section>

        )
    }
}

Signup.propTypes = {
    registerUser: PropTypes.func.isRequired,
    auth: PropTypes.object.isRequired,
    errors: PropTypes.object.isRequired
  };
  const mapStateToProps = state => ({
    auth: state.auth,
    errors: state.errors
  });

  export default connect(
    mapStateToProps,
    { registerUser }
  )(withRouter(Signup));
