        }
      }
      this.setState({Addnewroom})
   }
    handleInputChangeAmenities(e){ 
      let Addnewroom ={
        ...this.state.Addnewroom,
      Amenities:{
        ...this.state.Addnewroom.Amenities,
        [e.target.name]:e.target.checked
      }
    }
      this.setState({Addnewroom})
  }

    handleInputChange(e){   
      let Addnewroom ={
          ...this.state.Addnewroom,
          [e.target.name]: e.target.value
      }
      this.setState({Addnewroom})
  }
  handleInputChangeroomaterules(e){   
